/* Table for IMDB_User */CREATE TABLE "IMDB_User" ( "UserID" VARCHAR2(10) PRIMARY KEY, "FirstName" VARCHAR2(15) NOT NULL, "LastName" VARCHAR2(15) NOT NULL, "Gender" CHAR(1) NOT NULL, "Birthdate" DATE, "Birthplace" VARCHAR2(10), "Profile_Pic" VARCHAR2(100), "Email" VARCHAR2(30) NOT NULL );/* Table for Movie */CREATE TABLE "Movie" ( "Title" VARCHAR2(30) NOT NULL, "Release_Year" NUMBER NOT NULL, "Genre" VARCHAR2(30) NOT NULL, "Production_Cost" NUMBER, "Production_Contract" NUMBER, "Scenes_ID" NUMBER, "Serial_Number" VARCHAR2(5) PRIMARY KEY, "Awards" NUMBER, "Director" VARCHAR2(15) NOT NULL );/* Table for Production (multivalued attribute) */CREATE TABLE "Production" ( "Company" VARCHAR2(30) NOT NULL, "Contract_Num" NUMBER PRIMARY KEY, "Movie" VARCHAR2(5), "TV_Series" NUMBER );/* Table for Actor_Cast Relationship */CREATE TABLE "Actor_Cast" ( "Movie_ID" VARCHAR2(5) NOT NULL, "Actor_ID" VARCHAR2(15) NOT NULL, "Character_name" VARCHAR2(30) );/* Table for Awards (multivalued attribute) */CREATE TABLE "Awards" ( "Awards_ID" NUMBER PRIMARY KEY, "Categories_won" VARCHAR2(100), "Categories_nominated" VARCHAR2(100) NOT NULL, "Events" VARCHAR2(500), "Movie_ID" VARCHAR2(5), "TV_Series_ID" NUMBER, "Director_ID" VARCHAR2(15), "Actor_ID" VARCHAR2(15) );/* Table for Scenes Relationship */CREATE TABLE "Scenes" ( "Scenes_ID" NUMBER PRIMARY KEY, "Parent_Movie" VARCHAR2(5) NOT NULL, "SceneNum" NUMBER NOT NULL );/* Table for Review */CREATE TABLE "Review" ( "Review_ID" VARCHAR2(10) PRIMARY KEY, "Publish_Date" DATE NOT NULL, "Author" VARCHAR2(10) NOT NULL, "Textual_Content" VARCHAR2(2000), "NumStars" NUMBER NOT NULL, "NumVotes" NUMBER NOT NULL, "Content_ID" VARCHAR2(10), "Movie_Subject" VARCHAR2(5), "TV_Subject" NUMBER );/* Table for Reviews Relationship */CREATE TABLE "Reviews" ( "UserID" VARCHAR2(10) NOT NULL, "Review_ID" VARCHAR2(10) NOT NULL );/* Supertype Person */CREATE TABLE "Person" ( "PID" VARCHAR2(15) PRIMARY KEY, "FirstName" VARCHAR2(15) NOT NULL, "LastName" VARCHAR2(15) NOT NULL, "Gender" CHAR(1), "Birthdate" DATE, "Birthplace" VARCHAR2(10), "Photo" NUMBER, "Marriage_ID" NUMBER, "Guardian_Child_ID" NUMBER ); //* Table for Marriage Relationship */CREATE TABLE "Marriage" ( "Marriage_ID" NUMBER PRIMARY KEY, "Spouse1" VARCHAR2(15) NOT NULL, "Spouse2" VARCHAR2(15) NOT NULL, "Years_Married" NUMBER );/* Table for Guardian_Child Relationship */CREATE TABLE "Guardian_Child" ( "Guardian_Child_ID" NUMBER PRIMARY KEY, "Guardian" VARCHAR2(15) NOT NULL, "Child" VARCHAR2(15) NOT NULL );/* Table for Birthplace (multivalued attribute) */CREATE TABLE "Birth_place" ( "Place_ID" VARCHAR2(10), "Nation" VARCHAR2(20) NOT NULL, "Province" VARCHAR2(20), "State" VARCHAR2(20) );/* Table for Director */CREATE TABLE "Director" ( "Awards" NUMBER, "Movie" VARCHAR2(5), "PID" VARCHAR2(15) PRIMARY KEY, "FirstName" VARCHAR2(15) NOT NULL, "LastName" VARCHAR2(15) NOT NULL, "Gender" CHAR(1), "Birthdate" DATE, "Birthplace" VARCHAR2(10), "Photo" NUMBER, "Marriage_ID" NUMBER, "Guardian_Child_ID" NUMBER );/* Table for Actor */CREATE TABLE "Actor" ( "Awards" NUMBER, "PID" VARCHAR2(15) PRIMARY KEY, "FirstName" VARCHAR2(15) NOT NULL, "LastName" VARCHAR2(15) NOT NULL, "Gender" CHAR(1), "Birthdate" DATE, "Birthplace" VARCHAR2(10), "Photo" NUMBER, "Marriage_ID" NUMBER, "Guardian_Child_ID" NUMBER );/* Table for TV_Cast Relationship */CREATE TABLE "TV_Cast" ( "TV_Series" NUMBER NOT NULL, "Actor" VARCHAR2(15) NOT NULL, "Role" VARCHAR2(15) NOT NULL, "Character_name" VARCHAR2(30) NOT NULL );/* Table for Ep_Cast */CREATE TABLE "Ep_Cast" ( "Actor" VARCHAR2(15) NOT NULL, "Ep_Number" NUMBER NOT NULL, "Reg_or_Guest" VARCHAR2(7) NOT NULL );/* Table for Photo */CREATE TABLE "Photo" ( "PhotoID" NUMBER PRIMARY KEY, "Name" VARCHAR2(15) NOT NULL, "Author" VARCHAR2(15) NOT NULL, "Description" VARCHAR2(50), "Profile_or_personal" VARCHAR2(8) NOT NULL );/* Table for TV_Series */CREATE TABLE "TV_Series" ( "TV_Series_ID" NUMBER PRIMARY KEY, "Name" VARCHAR2(15) NOT NULL, "TV_Network" VARCHAR2(15) NOT NULL, "Production" NUMBER NOT NULL, "Num_Episodes" NUMBER NOT NULL );/* Table for Episode */CREATE TABLE "Episode" ( "Ep_Number" NUMBER NOT NULL, "Title" VARCHAR2(15) NOT NULL, "Ep_Length" NUMBER NOT NULL, "TV_Series" NUMBER NOT NULL );/* Table for RComment */CREATE TABLE "RComment" ( "Post_date" DATE, "Content_ID" VARCHAR2(10) NOT NULL, "Author" VARCHAR2(30) NOT NULL, "Review_ID" NUMBER NOT NULL, "Comment_ID" VARCHAR2(5) PRIMARY KEY );/* Table for Comments Relationship */CREATE TABLE "Comments" ( "Review_ID" VARCHAR2(10), "Comment_List" VARCHAR2(1000) );/* Table for Vote */CREATE TABLE "Vote" ( "Helpfulness" NUMBER NOT NULL, "Review_ID" VARCHAR2(10) NOT NULL, "Author" VARCHAR2(10) NOT NULL );/* Table for Scene */CREATE TABLE "Scene" ( "SceneNum" NUMBER PRIMARY KEY );							/* Table for Content */							CREATE TABLE "Content" ( "Content_ID" VARCHAR2(10) PRIMARY KEY, "Review_ID" VARCHAR2(10) NOT NULL, "Textual_Content" VARCHAR2(2000), "Picture" VARCHAR2(2000), "Video" VARCHAR2(2000) );--------------------------------------------------------------------------------------REM INSERTING into JJENG."Movie"SET DEFINE OFF;--------------------------------------------------------------------------------------REM INSERTING into JJENG."Director"SET DEFINE OFF;--------------------------------------------------------------------------------------REM INSERTING into JJENG."Actor"SET DEFINE OFF;--------------------------------------------------------------------------------------REM INSERTING into JJENG."Actor_Cast"SET DEFINE OFF;--------------------------------------------------------------------------------------REM INSERTING into JJENG."Review"SET DEFINE OFF;--------------------------------------------------------------------------------------REM INSERTING into JJENG."IMDB_User"SET DEFINE OFF;--------------------------------------------------------------------------------------